mapper.update:
UPDATE
employeeinfo
SET
company = #{company}, kind = #{kind}, joind = #{joind}, endd = #{endd}, name = #{name}, gender = #{gender}, birthday = #{birthday}, phone1 = #{phone1}, phone2 = #{phone2}, address = #{address}, garage = #{garage}, bosum = #{bosum}, bobuj = #{bobuj}, drvl = #{drvl}, busl = #{busl}, memo = #{memo}, bank = #{bank}, gye = #{gye}, gyename = #{gyename}, trash = #{trash}, img = #{img}, id = #{id}
WHERE
id = #{id};

mapper.select:
SELECT
FROM employeeinfo
LEFT JOIN vehicleinfo
ON employeeinfo.id = vehicleinfo.id
WHERE employeeinfo.name = #{name}
ORDER BY name;
